(()=>{"use strict";Element.prototype.addClass=function(t){return this.classList.add(t),this},Element.prototype.show=function(t){return this.style.display="block",this},Element.prototype.hide=function(t){return this.style.display="none",this},Element.prototype.removeClass=function(t){return this.classList.remove(t),this},Element.prototype.on=function(t,e){return t.split(" ").forEach((t=>this.addEventListener(t,e))),this},document.querySelector.bind(document)})();